mapper.select:
select * from dictator_resource resource where exists ( select 1 from dictator_role_resource rr where rr.resource_id = resource.id and rr.role_id in
#{roleId}
)

SELECT rr.role_id ,group_concat(res.resource_name) AS permissions FROM dictator_resource res LEFT JOIN dictator_role_resource rr ON res.id = rr.resource_id where rr.role_id in
#{roleId}
group by rr.role_id

select * from dictator_resource resource
where exists
(
select 1 from dictator_role_resource rr where rr.resource_id = resource.id
and rr.role_id = #{roleId}
)
and parent_id = #{parentId}
